  `unit_code` varchar(32) NOT NULL DEFAULT '000000000' COMMENT '组织机构代码',
  `fonds_no` varchar(10) NOT NULL DEFAULT '00000' COMMENT '全宗号',
  `year` varchar(10) NOT NULL COMMENT '年度',
