-- ----------------------------
-- Table structure for collect
-- ----------------------------
DROP TABLE IF EXISTS `collect`;
CREATE TABLE `collect` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '自增主键',
  `title` varchar(50) NOT NULL COMMENT '信息标题',
  `content` mediumtext NOT NULL COMMENT '内容',
  `copy_content` mediumtext NOT NULL COMMENT '原始内容',
  `category` varchar(20) NOT NULL COMMENT '信息类别 一般信息、专报信息、约稿信息、其它信息',
  `send_dept` int(11) NOT NULL COMMENT '报送单位',
  `status` tinyint(2) NOT NULL COMMENT '0:草稿;1:已报送;2:未通过审核;3:已审核;(审批状态)',
  `rating` tinyint(2) NOT NULL COMMENT '评分',
  `audit_user` int(11) NOT NULL COMMENT '审查员',
  `audit_time` int(11) NOT NULL COMMENT '审核时间',
  `tag` text NOT NULL COMMENT '标签',
  `send_user` int(11) NOT NULL COMMENT '报送人员',
  `send_time` int(11) NOT NULL COMMENT '报送日期',
  `create_user` int(11) NOT NULL COMMENT '创建人',
  `create_time` int(11) NOT NULL COMMENT '创建日期',
  `criteria_id` int(11) NOT NULL DEFAULT '0' COMMENT '评分类别',
  `collect_public` int(1) NOT NULL COMMENT '是否公开',
  `sign_user` varchar(200) NOT NULL COMMENT '签发人',
  `telephone` varchar(200) NOT NULL COMMENT '联系方式',
  `comment` varchar(200) NOT NULL COMMENT '审核不通过时意见',
  `custom_fields` text NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=16 DEFAULT CHARSET=utf8;

