-- ----------------------------
-- Table structure for daemon_config
-- ----------------------------
DROP TABLE IF EXISTS `daemon_config`;
CREATE TABLE `daemon_config` (
  `server_id` tinyint(3) unsigned NOT NULL AUTO_INCREMENT COMMENT '自增唯一ID',
