-- ----------------------------
-- Table structure for edu_apply_students
-- ----------------------------
DROP TABLE IF EXISTS `edu_apply_students`;
CREATE TABLE `edu_apply_students` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '自增主键',
  `name` varchar(50) NOT NULL COMMENT '学生姓名',
  `sex` tinyint(1) NOT NULL COMMENT '学生性别(0:男；1:女；)',
  `id_number` varchar(20) NOT NULL COMMENT '身份证号',
  `school_category` varchar(50) NOT NULL COMMENT '学校类别',
  `register_status` tinyint(1) NOT NULL COMMENT '报名状态（1：已录取 2：末录取 0：待录取）',
  `register_tel` varchar(50) NOT NULL COMMENT '注册手机号码',
  `register_date` int(11) NOT NULL COMMENT '报名时间',
  `school_year` varchar(50) NOT NULL COMMENT '学年',
  `create_user` int(11) NOT NULL COMMENT '创建人',
  `check_user` int(11) NOT NULL COMMENT '审核人',
  `form_id` char(36) NOT NULL COMMENT '表单Id',
  `form_data_id` int(11) NOT NULL COMMENT '表单数据id',
  `this_city` tinyint(1) NOT NULL COMMENT '是否为本市户口(是：1 否：0）',
  `custom_fields` text NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

