-- ----------------------------
-- Table structure for edu_arrange_course
-- ----------------------------
DROP TABLE IF EXISTS `edu_arrange_course`;
CREATE TABLE `edu_arrange_course` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '自增唯一id',
  `academic_year` varchar(50) NOT NULL COMMENT '学年',
  `semester` varchar(50) NOT NULL COMMENT '学期',
  `section` varchar(10) NOT NULL COMMENT '节次',
  `branch_school` varchar(20) NOT NULL COMMENT '校区',
  `grade` varchar(10) NOT NULL COMMENT '年级',
  `class` varchar(10) NOT NULL COMMENT '班级',
  `afternoon` varchar(10) NOT NULL COMMENT '作息时间段（上午、下午）',
  `monday` varchar(50) NOT NULL COMMENT '星期一',
  `tuesday` varchar(50) NOT NULL COMMENT '星期二',
  `wednesday` varchar(50) NOT NULL COMMENT '星期三',
  `thursday` varchar(50) NOT NULL COMMENT '星期四',
  `friday` varchar(50) NOT NULL COMMENT '星期五',
  `saturday` varchar(50) NOT NULL COMMENT '星期六',
