  `course_description` longtext NOT NULL COMMENT '科目描述 ',
  `create_user` int(11) NOT NULL COMMENT '创建人',
  `grades` varchar(40) NOT NULL COMMENT '适用年级，多选年级',
  `course_type` varchar(200) NOT NULL COMMENT '课程类别',
