DROP TABLE IF EXISTS `edu_message`;
CREATE TABLE `edu_message` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '自增唯一id',
  `from_uid` int(10) unsigned NOT NULL COMMENT '发信人uid',
  `to_uid` int(10) unsigned NOT NULL COMMENT '收信人uid',
  `remind_flag` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '提醒标记(0-收信人已读,1-已提醒,2-收信人未读)',
  `delete_flag` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '删除标记(1-收信人已删除,2-发信人已删除)',
  `send_time` int(10) unsigned NOT NULL COMMENT '发送时间',
  `msg_type` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '消息类型(0-微讯网页消息,1-即时通讯离线消息,3-微讯移动版,5-微讯移动iphone版,6-微讯移动android版)',
  `edu_msg_type` tinyint(2) NOT NULL COMMENT '判断消息类型是单聊还是群聊（0普通消息1学生群聊2老师群聊3家长群聊）',
  `read_user` text NOT NULL COMMENT '读取消息用户（老师）',
  `content` varchar(5000) NOT NULL COMMENT '发送内容',
  `from_people_type` tinyint(1) NOT NULL DEFAULT '0' COMMENT '发信人类型。0教师1学生2家长',
  `to_people_type` tinyint(1) NOT NULL DEFAULT '0' COMMENT '收信人类型。0教师1学生2家长',
  `read_parents` text COMMENT '读取消息用户（家长）',
  `read_student` text COMMENT '读取消息用户（学生）',
  `from_key` varchar(20) DEFAULT NULL,
  `to_key` varchar(20) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `from_uid` (`from_uid`),
  KEY `to_uid` (`to_uid`),
  KEY `send_time` (`send_time`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='微讯信息表';

