-- ----------------------------
-- Table structure for edu_sms
-- ----------------------------
DROP TABLE IF EXISTS `edu_sms`;
CREATE TABLE `edu_sms` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '自增唯一ID',
  `to_id` int(11) NOT NULL COMMENT '收信人USER_ID',
  `remind_flag` tinyint(1) NOT NULL DEFAULT '1' COMMENT '接收标记(0-已阅读,1-未阅读,2-未阅读已弹出)',
  `delete_flag` tinyint(1) NOT NULL DEFAULT '0' COMMENT '删除标记(0-没删除,1-收信人删除,2-发信人删除)',
  `body_id` int(11) NOT NULL DEFAULT '0' COMMENT 'sms_body表对应记录的ID',
  `remind_time` int(11) NOT NULL DEFAULT '0' COMMENT '最近一次提醒时间',
  `people_type` tinyint(1) NOT NULL DEFAULT '0' COMMENT '接收标记(0-老师,1-学生,2-家长)',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of edu_sms
-- ----------------------------

-- ----------------------------
-- Table structure for edu_sms_body
-- ----------------------------
DROP TABLE IF EXISTS `edu_sms_body`;
CREATE TABLE `edu_sms_body` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '自增唯一ID',
  `from_id` int(11) NOT NULL COMMENT '发信人USER_ID',
  `sms_type` tinyint(1) NOT NULL DEFAULT '0' COMMENT '短信类型',
  `content` text NOT NULL COMMENT '短信内容',
  `send_time` int(10) unsigned NOT NULL COMMENT '发送时间',
  `remind_url` varchar(255) NOT NULL DEFAULT '' COMMENT '相关的URL',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='内部短信主体表';

-- ----------------------------
-- Records of edu_sms_body
-- ----------------------------

-- ----------------------------
-- Table structure for edu_sms_template
-- ----------------------------
DROP TABLE IF EXISTS `edu_sms_template`;
CREATE TABLE `edu_sms_template` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '短信模版编号',
  `template_name` varchar(50) NOT NULL COMMENT '模版名称',
  `template_content` varchar(300) NOT NULL COMMENT '模版内容',
  `create_user` int(11) NOT NULL COMMENT '发布人',
  `template_type` tinyint(1) NOT NULL DEFAULT '0' COMMENT '0短信模版；1是信息通知模版',
