DROP TABLE IF EXISTS `edu_students_ext`;
CREATE TABLE `edu_students_ext` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT '唯一自增主键ID',
  `student_id` int(11) NOT NULL COMMENT '学生id',
  `native_place` varchar(100) NOT NULL COMMENT '籍贯',
  `english_name` varchar(100) NOT NULL COMMENT '英文名',
  `name_spelling` varchar(100) NOT NULL COMMENT '姓名拼音',
  `nation` varchar(100) NOT NULL COMMENT '民族',
  `semeter` varchar(100) NOT NULL COMMENT '届（涉及学生数据的，会需要届，例如2015届，以毕业时间为届，添加用户的时候，自动生成该数据）',
  `nationnality` varchar(200) NOT NULL COMMENT '国籍/地区',
  `id_number` varchar(20) NOT NULL COMMENT '身份证号',
  `is_pioneers` varchar(20) NOT NULL COMMENT '是否少先队员（0不是，1是）',
  `religion` varchar(100) NOT NULL COMMENT '政治面貌',
  `religious` varchar(100) NOT NULL COMMENT '信仰宗教',
  `blood_type` varchar(20) NOT NULL COMMENT '血型',
  `school_year` varchar(10) NOT NULL COMMENT '入学年份',
  `class` varchar(100) NOT NULL COMMENT '班级',
  `grade` varchar(100) NOT NULL COMMENT '年级',
  `present_addr` varchar(200) NOT NULL COMMENT '现住址',
  `permanent_addr` varchar(200) NOT NULL COMMENT '户口所在地',
  `account_property` varchar(100) NOT NULL COMMENT '户口性质',
  `postal_code` varchar(6) NOT NULL COMMENT '邮政编码',
  `community` varchar(200) DEFAULT NULL COMMENT '所在社区',
  `postal_addr` varchar(200) NOT NULL COMMENT '通讯地址',
  `student_number` varchar(50) NOT NULL COMMENT '学籍号',
  `motto` text NOT NULL COMMENT '座右铭',
  `introduce` text NOT NULL COMMENT '个人简介',
  `school_month` varchar(10) NOT NULL COMMENT '入学月份',
  `bord_place` text NOT NULL COMMENT '出生地',
  `id_type` varchar(200) NOT NULL COMMENT '身份证类',
  `is_gatqy` varchar(50) NOT NULL COMMENT '港澳台侨外',
  `health_status` varchar(200) NOT NULL COMMENT '健康状况',
  `album` text NOT NULL COMMENT '照片',
  `user_name` varchar(200) NOT NULL COMMENT '曾用名',
  `id_validity` int(11) NOT NULL COMMENT '身份证件有效期',
  `sassitant_no` varchar(200) NOT NULL COMMENT '学籍辅号',
  `class_no` varchar(200) NOT NULL COMMENT '班内学号',
  `entrance` varchar(200) NOT NULL COMMENT '入学方式',
  `study_way` varchar(50) NOT NULL COMMENT '就读方式',
  `s_source` varchar(200) NOT NULL COMMENT '学生来源',
  `home_addr` varchar(200) NOT NULL COMMENT '家庭地址',
  `home_url` varchar(255) NOT NULL COMMENT '主页地址',
  `is_one_child` tinyint(2) NOT NULL COMMENT '是否独生子女',
  `is_preschool` tinyint(2) NOT NULL COMMENT '是否受过学前教育',
  `is_left_behide_child` varchar(50) NOT NULL COMMENT '是否留守儿童',
  `is_sq_child` tinyint(2) NOT NULL COMMENT '是否进城务工人员随迁子女',
  `is_orphan` tinyint(2) NOT NULL COMMENT '是否孤儿',
  `is_special_child` tinyint(2) NOT NULL COMMENT '是否烈士或优抚子女',
  `is_sbjd` varchar(50) NOT NULL COMMENT '随班就读',
  `disable_type` varchar(50) NOT NULL COMMENT '残疾类型',
  `is_gov_buy` tinyint(2) NOT NULL COMMENT '是否由政府购买学位',
  `is_need_support` tinyint(2) NOT NULL COMMENT '是否需要申请资助',
  `is_enjoy_supplement` tinyint(2) NOT NULL COMMENT '是否享受一补',
  PRIMARY KEY (`id`),
  UNIQUE KEY `student_id` (`student_id`)
) ENGINE=InnoDB AUTO_INCREMENT=17 DEFAULT CHARSET=utf8 COMMENT='学生扩展信息表';

