CREATE TABLE `edu_student_leave` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `user_id` varchar(50) NOT NULL COMMENT '学生姓名',
  `grade` varchar(50) NOT NULL COMMENT '年级',
  `class` varchar(50) NOT NULL COMMENT '班级',
  `academic_year` varchar(20) NOT NULL COMMENT '学年',
  `semester` varchar(10) NOT NULL COMMENT '学期',
  `prents_id` int(11) NOT NULL COMMENT '家长用户名',
