  `leave_days` varchar(3) NOT NULL COMMENT '请假天数',
  `leave_type` varchar(2) NOT NULL COMMENT '请假类型',
  `leave_reason` text NOT NULL COMMENT '请假原因',
  `approval` int(11) NOT NULL COMMENT '审批人',
