INSERT INTO `timeline_item` VALUES ('44', '3', '实现大江截流', '878918400', '878918400', '三峡工程工程实现大江截流，标志着为期５年的一期工程胜利完成，三峡工程转入二期工程建设。 \n');
INSERT INTO `timeline_item` VALUES ('45', '3', '三峡永久船闸一期开挖工程通过竣工验收', '893088000', '893088000', '三峡永久船闸一期开挖工程通过竣工验收，工程质量合格率达１００％，，优良率达８２.６％，总体质量完全达到设计要求。 \n');
INSERT INTO `timeline_item` VALUES ('46', '3', '长江三峡临时船闸正式通航', '893952000', '893952000', '经过５年的建设，长江三峡临时船闸正式通航。这是目前世界上最大的双线五级船闸。 \n');
INSERT INTO `timeline_item` VALUES ('47', '3', '永久船闸主体开挖工程结束', '938880000', '938880000', '三峡工程永久船闸南线五闸首保护层最后一方石碴被运往碴场，至此，由武警水电部队第四支队担负的永久船闸南线开挖全线告捷，这标志着永久船闸主体开挖工程圆满结束。');
INSERT INTO `timeline_item` VALUES ('48', '3', '三峡工程左岸电站厂房首台机组基础环在一号机安装就位', '948643200', '948643200', '三峡工程左岸电站厂房首台机组基础环在一号机安装就位，标志着三峡左岸电站厂房施工由土建施工为主转入土建与机组埋件安装并举阶段。');
INSERT INTO `timeline_item` VALUES ('49', '3', '三峡工程永久性船闸首扇反弧门安装成功', '1320595200', '1320595200', '三峡工程永久性船闸首扇反弧门在闸首中南竖井内安装成功。');
INSERT INTO `timeline_item` VALUES ('50', '3', '三峡工程首台发电机组转轮吊装成功', '1016294400', '1016294400', '三峡工程首台发电机组转轮吊装成功。转轮的顺利安装为三峡工程在２００３年实现首批机组发电创造了条件。');
INSERT INTO `timeline_item` VALUES ('51', '3', '泄洪坝段建成', '1035129600', '1035129600', '三峡大坝最关键的泄洪坝段已经全部建成，全线达到海拔１８５米大坝设计高程。 \n');
INSERT INTO `timeline_item` VALUES ('52', '3', '三峡二期大坝全线封顶', '1035561600', '1035561600', '全长１.６公里的三峡二期大坝全线封顶，整段大坝都已升高到海拔１８５米设计坝顶高程。 \n');
INSERT INTO `timeline_item` VALUES ('53', '3', '进入总装阶段', '1036598400', '1036598400', '世界上最大的水轮发电机组转子在三峡工地成功吊装，标志着三峡首台机组大件安装基本完成，从此进入总装阶段。');
INSERT INTO `timeline_item` VALUES ('54', '3', '三峡大坝中的闸门按计划准时启动', '1054396800', '1054396800', '三峡大坝中的闸门按计划准时启动，三峡工程正式下闸蓄水。三峡工程船闸全长６.４公里，其中船闸主体部分１. ６公里，引航道４.８公里。三峡船闸系双线５级梯级船闸，其工程规模居世界之最。 \n');
INSERT INTO `timeline_item` VALUES ('55', '3', '三峡工程进入三期工程', '1073577600', '1073577600', '三峡三期工程至２００９年历时６年，期间将完成右岸大坝和右岸电站建设，修建世界上最大的升船机，有２０台单机７０万千瓦的机组投产。');
INSERT INTO `timeline_item` VALUES ('56', '3', '签订合同', '1113580800', '1113580800', '三峡地下电站近日通过国家环保部门审评，其右岸地下电站的勘测设计与施工合同已签订。 \n');
INSERT INTO `timeline_item` VALUES ('57', '3', '三峡工程三期库底清理工作进入全面实施阶段', '1139500800', '1139500800', '三峡工程三期库底清理工作进入全面实施阶段。 ');
INSERT INTO `timeline_item` VALUES ('58', '3', '三峡大坝进入最后的施工建设阶段', '1143561600', '1143561600', '三峡大坝进入最后的施工建设阶段，离大坝全部建成只剩下约７．８万立方米混凝土。三峡大坝全长２３０９米，混凝土总方量为１６１０万立方米，是世界上规模最大的大坝，设计坝顶海拔高程１８５米。 \n');
INSERT INTO `timeline_item` VALUES ('59', '3', '完工', '1148054400', '1148054400', '三峡大坝全线到顶185米高程，举世瞩目的世界第一水坝宣告完工。\n');

