-- ----------------------------
-- Table structure for user_delegate
-- ----------------------------
DROP TABLE IF EXISTS `user_delegate`;
CREATE TABLE `user_delegate` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `module` varchar(64) NOT NULL DEFAULT '' COMMENT '模块',
  `rule` text NOT NULL COMMENT '业务规则',
  `from_id` int(11) NOT NULL COMMENT '委托人ID',
  `to_id` int(11) NOT NULL COMMENT '被委托人ID',
  `begin_time` int(11) NOT NULL DEFAULT '0' COMMENT '开始时间',
  `end_time` int(11) NOT NULL DEFAULT '0' COMMENT '结束时间',
  `status` char(1) NOT NULL DEFAULT '1' COMMENT '状态(0-关闭,1-开启)',
  PRIMARY KEY (`id`),
  KEY `module` (`module`),
  KEY `from_id` (`from_id`),
  KEY `to_id` (`to_id`),
  KEY `begin_time` (`begin_time`),
  KEY `end_time` (`end_time`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

