  CONSTRAINT `user_desktop_fk1` FOREIGN KEY (`user_id`) REFERENCES `user` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `user_desktop_fk2` FOREIGN KEY (`portlet_id`) REFERENCES `desktop_portlet` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
