DROP TRIGGER IF EXISTS `trigger_message_insert`;
DELIMITER ;;
CREATE TRIGGER `trigger_message_insert` AFTER INSERT ON `message` FOR EACH ROW begin
select id, user1, user2 into @id, @user1, @user2 from message_relations where (user1=NEW.create_user and user2=NEW.to_uid) or (user1=NEW.to_uid and user2=NEW.create_user) limit 1;
if @id > 0 then
	set @count1 := (select count(id) from message where create_user=@user1 and to_uid=@user2);
	set @count2 := (select count(id) from message where create_user=@user2 and to_uid=@user1);
	update message_relations set counter1=@count1, counter2=@count2, msg_id=NEW.id, update_time=NEW.create_time where id=@id;
else
	set @user1 = new.create_user;
	set @user2 = new.to_uid;
	set @count1 = 1;
	set @count2 = 0;
	insert into message_relations (msg_id, update_time, user1, user2, counter1, counter2) values(NEW.id, NEW.create_time, @user1, @user2, @count1, @count2);
end if;
end
;;
DELIMITER ;
